mapper.select:
select count(*) > 0
from EMPLOYEES
where PHONE = #{phone};

select count(*) > 0
from EMPLOYEES
where ID_CARD = #{idCard};

select emp_id, emp_name, dept_id, position, phone, email, id_card, is_manager, create_time, update_time
from EMPLOYEES
where EMP_ID = #{empId};

SELECT COUNT(*)
FROM employees e
INNER JOIN accounts a ON e.emp_id = a.account_id
INNER JOIN departments d ON e.dept_id = d.dept_id


        AND e.dept_id = #{deptId}


        AND a.is_active = #{isActive}


        AND e.is_manager = #{isManager}


        AND e.emp_name LIKE CONCAT('%', #{empName}, '%')

SELECT a.account_id, a.is_active, a.last_login_time, e.emp_id, e.emp_name, e.position, e.phone, e.email, e.id_card, e.is_manager, e.create_time, d.dept_id, d.dept_name FROM employees e INNER JOIN accounts a ON e.emp_id = a.account_id INNER JOIN departments d ON e.dept_id = d.dept_id
AND e.dept_id = #{deptId}
AND a.is_active = #{isActive}
AND e.is_manager = #{isManager}
AND e.emp_name LIKE CONCAT('%', #{empName}, '%')
ORDER BY a.account_id ASC
LIMIT ${(pageNum - 1) * pageSize}, #{pageSize}
