mapper.select:
select room_id, room_name, room_type, capacity, location, room_status, image_url, create_time, update_time
from MEETING_ROOMS
where ROOM_NAME = #{roomName};

SELECT COUNT(DISTINCT mr.room_id)
FROM meeting_rooms mr
WHERE 1=1

    AND mr.room_name LIKE CONCAT('%', #{roomName}, '%')


    AND mr.room_type = #{roomType}


    AND mr.capacity >= #{minCapacity}


    AND mr.capacity <= #{maxCapacity}


    AND mr.location LIKE CONCAT('%', #{location}, '%')


    AND mr.room_status = #{roomStatus}




        AND EXISTS (
        SELECT 1 FROM room_equipment re
        WHERE re.room_id = mr.room_id
        AND re.equipment_id = #{eqId}
        )

SELECT room_id, room_name, room_type, capacity, location, room_status, image_url, create_time, update_time FROM meeting_rooms mr WHERE 1=1
AND mr.room_name LIKE CONCAT('%', #{roomName}, '%')
AND mr.room_type = #{roomType}
AND mr.capacity >= #{minCapacity}
AND mr.capacity <= #{maxCapacity}
AND mr.location LIKE CONCAT('%', #{location}, '%')
AND mr.room_status = #{roomStatus}
AND EXISTS ( SELECT 1 FROM room_equipment re WHERE re.room_id = mr.room_id AND re.equipment_id = #{eqId} )
ORDER BY mr.room_id
LIMIT ${(pageNum - 1) * pageSize}, #{pageSize}
