mapper.update:
update NOTIFICATIONS
set IS_READ = true
where RECEIVER_ID = #{accountId};

mapper.select:
select notification_id, sender_id, receiver_id, notification_type, title, content, related_id, is_read, create_time, read_time
from NOTIFICATIONS
where 1=1

    AND RECEIVER_ID = #{accountId}

ORDER BY CREATE_TIME DESC

LIMIT ${(pageRequest.pageNum - 1) * pageRequest.pageSize}, #{pageRequest.pageSize}

select count(*)
from NOTIFICATIONS
where RECEIVER_ID = #{accountId} and IS_READ = false;
