mapper.update:
UPDATE reservations SET reservation_status = #{reservationStatus}, approval_account_id = #{approvalAccountId}, approval_time = CURRENT_TIMESTAMP()
, reject_reason = #{rejectReason}
WHERE reservation_id = #{reservationId}

mapper.select:
select *
from RESERVATIONS
where ROOM_ID = #{roomId} and ACCOUNT_ID = #{accountId} and RESERVATION_DATE = #{reservationDate} and START_TIME = #{startTime} and END_TIME = #{endTime} and RESERVATION_STATUS = 'PENDING';

SELECT COUNT(*)
FROM reservations


        AND account_id = #{accountId}


        AND reservation_date = #{reservationDate}


        AND reservation_status = #{reservationStatus}

SELECT reservation_id, room_id, account_id, emp_name, meeting_topic, meeting_desc, reservation_date, start_time, end_time, reservation_status, approval_account_id, approval_time, reject_reason, create_time, update_time FROM reservations
AND account_id = #{accountId}
AND reservation_date = #{reservationDate}
AND reservation_status = #{reservationStatus}
ORDER BY create_time DESC
LIMIT ${(pageNum - 1) * pageSize}, #{pageSize}

select reservation_id, room_id, account_id, emp_name, meeting_topic, meeting_desc, reservation_date, start_time, end_time, reservation_status, approval_account_id, approval_time, reject_reason, create_time, update_time
from RESERVATIONS
where RESERVATION_ID = #{reservationId};

select reservation_id, room_id, account_id, emp_name, meeting_topic, meeting_desc, reservation_date, start_time, end_time, reservation_status, approval_account_id, approval_time, reject_reason, create_time, update_time
from RESERVATIONS
where ROOM_ID = #{roomId}
and RESERVATION_STATUS in ('PENDING', 'APPROVED');
